import React from 'react'
import Comments from "./Comments"
export default class Post extends React.Component{

    state = {
        note: this.props.note.note,
        edit: false,
    }

handleEdit = (e) => {
    e.preventDefault()
    this.setState({
        edit: !this.state.edit,
        note
    })
}



    render() {

       return(
           <div key={this.props.note.id} className='note'>

               <span>
        {/* NOTE TITLE */}
               <h3 className='title'>{this.props.note.title}</h3>
               <br></br>

        {/* DELETE NOTE */}
               <button onClick={() => this.props.deleteNote(this.props.note)}>X</button>
               </span>

               {/* <img src={this.props.note.image}></img> */}

        {/* EDIT NOTE */}
                {this.state.edit 
                ? <form onSubmit={(e)=> this.props.updatedNote(e)}>
                   <input 
                   type="text" 
                   defaultValue={this.state.note} 
                   name="updatedNote" 
                //    value={this.state.updatedNote}
                   onChange={(e)=>this.props.updateNote(e, e.target.value)}
                   ></input>
               </form>
                : <h4 onClick={(e)=> this.handleEdit(e)}>{this.state.note}</h4>}

                {/* <ul className='details'>
                {this.props.note.note}((note, key)=> <li key={note.id}> {note} </li>)}
                </ul>  */}

                <span>
               <button onClick={()=>this.props.archiveNote(!this.props.post.favorited, this.props.post,
                 this.props.post.favorited !== null ? alert("Archived!") : alert("Unarchived :(") )}>Archive</button> 
                <p>{this.props.note.datetime}</p>
                 </span>
           </div>
       )
    }

}
