import React, { Component } from 'react';

class Reminder extends Component {
    state = {
        reminder_text: this.props.reminder.reminder_text,
        edit: false,
        newNote: "",
    }
    render() {
        return (
            <div key={this.props.reminder.id} className='note'>
                <span>
                    {/* REMINDER TITLE */}
                    <h3 className='title'>{this.props.reminder.title}</h3>
                    <br></br>
                </span>
            </div>
        );
    }
}

export default Reminder;
