import React from 'react'
import Navbar from "../components/Navbar"
// import FavoritedContainer from "../containers/FavoritedContainer"
import NoteContainer from "../containers/NoteContainer"
// import FilteredContainer from "../containers/FilteredContainer"

import { BrowserRouter as Router, Route} from 'react-router-dom';


class App extends React.Component {

  state = {
    notes: [],
   }


   componentDidMount() {
    fetch('http://localhost:9292/notes/')
    .then(resp => resp.json())
    .then(noteData => this.setState({notes: noteData}))
  }

  addNote = (noteObj) => {
    // debugger
    let newNote = {
      title: noteObj.title,
      // image: noteObj.image,
      // archived: false,
      datetime: new Date().toLocaleString(),
      note: noteObj.note,
      // details: [],
    }


  let reqObj = {}
  reqObj.headers = {'Content-Type': 'Application/json'}
  reqObj.method = 'POST'
  reqObj.body = JSON.stringify(newNote)


  fetch('http://localhost:9292/notes/', reqObj)
  .then(resp => resp.json())
  .then(newNoteObj => {
    this.setState({notes: [newNoteObj, ...this.state.notes]})
    })
  }



 render() {


  return (
    <Router>
    <div>

        <Navbar  />
        <br/>
        <Route exact path="/" component={() => <NoteContainer  addNote={this.addNote}  notes={this.state.notes}/>} />


    </div>
  </Router>
  )



  }
}
export default App;
