import React from 'react'
import Navbar from "../components/Navbar"
// import FavoritedContainer from "../containers/FavoritedContainer"
import NoteContainer from "../containers/NoteContainer"
// import FilteredContainer from "../containers/FilteredContainer"

import { BrowserRouter as Router, Route} from 'react-router-dom';


class App extends React.Component {

  state = {
    notes: [],
    // note: ""
   }

  // GET REQUEST
   componentDidMount() {
    fetch('http://localhost:9292/notes/')
    .then(resp => resp.json())
    .then(noteData => this.setState({notes: noteData}))
  }

  // POST REQUEST
  addNote = (e, noteObj) => {
  e.preventDefault()
    let newNote = {
      title: noteObj.title,
      datetime: new Date().toLocaleString(),
      note: noteObj.note,
      // image: noteObj.image,
      // archived: false,
      // details: [],
    }

  let reqObj = {}
  reqObj.headers = {'Content-Type': 'Application/json'}
  reqObj.method = 'POST'
  reqObj.body = JSON.stringify(newNote)

  fetch('http://localhost:9292/notes/', reqObj)
  .then(resp => resp.json())
  .then(newNoteObj => {
    this.setState({notes: [newNoteObj, ...this.state.notes]})
    })
  }

// PATCH REQUEST (update note)
updateNote = (e, updatedNoteObj) => {
  e.preventDefault()
  console.log(updatedNoteObj)
    let updatedNote = {
      // title: noteObj.title,
      note: updatedNoteObj.note,
      // image: noteObj.image,
      // details: [],
    }

  let reqObj = {}
  reqObj.headers = {'Content-Type': 'Application/json'}
  reqObj.method = 'PATCH'
  reqObj.body = JSON.stringify(updatedNote)
updatedNote
  fetch(`http://localhost:9292/notes/${updatedNoteObj.id}`, reqObj)
  .then(resp => resp.json())
  .then(newNoteObj => {
    this.setState({notes: [newNoteObj, ...this.state.notes]})
    })
  }

// DELETE REQUEST
deleteNote = (noteObj) => {
    let newNotes = this.state.notes.filter(note => note.id !== noteObj.id)

    fetch(`http://localhost:9292/notes/${noteObj.id}`, {method: 'DELETE'})
    .then(resp => resp.json())
    .then(() => this.setState({notes: newNotes}))
  }

 render() {


  return (
    <Router>
    <div>

        <Navbar  />
        <br/>
        <Route exact path="/" component={() => 
        <NoteContainer  
        updateNote={this.updateNote} 
        addNote={this.addNote}  
        deleteNote={this.deleteNote} 
        notes={this.state.notes}/>} />


    </div>
  </Router>
  )



  }
}
export default App;
