import React from 'react'
import Reminder from '../components/Reminder'
import CreateReminderForm from "../components/CreateReminderForm"

class ReminderContainer extends React.Component {


    componentDidMount() {
        fetch("http://localhost:9292/reminders")
            .then(r => r.json())
            .then(reminder => this.state.reminders)
    }
    render() {

        return (
            <div>
                <CreateReminderForm addReminder={this.props.addReminder} />

                {this.state.reminders.map(reminder => <Reminder key={reminder.id} reminder={reminder} />)}
